import ArrowUpSVG from '~/images/arrow/up.svg?inline';

export default {
    components: { ArrowUpSVG },
    data: () => ({
        socialLinks: [
            {
                label: 'LinkedIn',
                href: 'https://www.linkedin.com/in/diane-bouffard-vercelli/'
            },
            {
                label: 'github',
                href: 'https://github.com/dianubv'
            },
            {
                label: 'email',
                href: 'diane.bouffardvercelli@gmail.com'
            },
            {
                label: 'IFT-website',
                href: 'https://dvic.devinci.fr/member/diane-bouffard-vercelli'
            }
        ]
    }),
    mounted() {
        const prefersReducedMotion = this.$prefersReducedMotion();
        const { footer, footerWrapper, footerContentTitle } = this.$refs;
        const gsap = this.$gsap;

        const resizeObserver = new ResizeObserver(
            this.setFooterWrapperHeightVar
        );

        resizeObserver.observe(footerWrapper);
        this.setFooterWrapperHeightVar();

        if (!prefersReducedMotion) {
            gsap.fromTo(
                footerContentTitle,
                { '--x-offset': '0%' },
                {
                    '--x-offset': '100%',
                    ease: 'none',
                    scrollTrigger: {
                        trigger: footer,
                        start: `bottom-=${footerContentTitle.offsetHeight} bottom`,
                        end: `bottom bottom`,
                        scrub: true
                    }
                }
            );
        }
    },
    methods: {
        setFooterWrapperHeightVar() {
            this.$gsap.set(this.$refs.footer, {
                '--wrapper-height': `${this.$refs.footerWrapper.offsetHeight}px`,
                onEnd: () => this.$locomotiveScroll.update()
            });
        },
        getCurrentYear() {
            return new Date().getFullYear();
        }
    }
};
